package ra.databinding.model.entity;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.*;@NoArgsConstructor@AllArgsConstructor@Getter@Setter@Entity@Table(name = "likes")public class Like {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    @ManyToOne    private Book book;    @ManyToOne    private User user;}